package core_Java;

public class J02_DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// primitive data types

		byte b = 127;
		short s = 12345;
		int i = 1234567891;
		long l = 1234567891011121131l;
		float f = 12.34f;
		double d = 12345.12345d;
		char c ='a';
		boolean bool = true;

		System.out.println(b);System.out.println(s);System.out.println(i);
		System.out.println(l);System.out.println(f);System.out.println(d);
		System.out.println(c);System.out.println(bool);
	}

}
